import React from 'react';
import styled from "styled-components";
import {CorrectedImage, Link, LinkArrow, Title} from "../../styles/common";

const CartTitle = styled(Title)`
  text-align: center;
  background-image: url("../../img/cart/title-background.jpg");
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  padding: 10rem ${props => props.theme.container.paddingX};
  margin-bottom: 5rem;
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    padding: 3rem ${props => props.theme.container.paddingX};
    margin-bottom: 2rem;
  }
`

const CartContent = styled.div`
  text-align: center;
  background: rgba(253, 176, 45, 0.16);;
  padding: 8rem ${props => props.theme.container.paddingX} 3rem ${props => props.theme.container.paddingX};
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    padding: 3rem ${props => props.theme.container.paddingX} 2rem ${props => props.theme.container.paddingX};
  }
  color: ${props => props.theme.colors.title};
  font-family: Roboto, sans-serif;
  font-size: 1.77778rem;
  font-weight: 700;
`

const CartOrdersList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`

const CartOrdersListItem = styled.div`
  display: flex;
  
`

const CartOrdersListItemImageContainer = styled.div`
  border-radius: 1.66667rem;
  background: #F9F8F8;
`

const CartSummaries = styled.div`
  margin-top: 2rem;
  display: flex;
  flex-direction: column;
  text-align: right;
  gap: 1.5rem;
`

function Cart() {
  const total = 26;
  const discount = 14;
  return (
    <div>
      <CartTitle>Cart</CartTitle>
      <CartContent>
        <CartOrdersList>
          <CartOrdersListItem>
            <CorrectedImage src="./img/shop/white-nuts.png" alt="Product name" />
          </CartOrdersListItem>
        </CartOrdersList>
        <CartSummaries>
          <div>Total Cost {total}$</div>
          <div>Discount {discount}$</div>
        </CartSummaries>
        <Link href="#" $marginTop="4rem" $color="#FFFFFF" $backgroundColor={props => props.theme.colors.title}>
          <div>To order</div>
          <LinkArrow>
            <img src="./img/common/arrow.svg" alt=""/>
          </LinkArrow>
        </Link>
      </CartContent>
    </div>
  );
}

export default Cart;
