import React from 'react';
import styled from "styled-components";
import {Button, ButtonArrow, Container, CorrectedImage, Subtitle, Title} from "../../styles/common";

const StyledAbout = styled.div`
  background: #F9F8F8;
  padding-top: 5rem;
  padding-bottom: 5rem;
`

const AboutInner = styled(Container)`
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 2rem;
`

const AboutImgContainer = styled.div`
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    display: none;
  }
`

const AboutContent = styled.div`
  max-width: 530px;
  width: 100%;
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    padding: 3rem 0;
    text-align: center;
    max-width: none;
    display: flex;
    flex-direction: column;
    align-items: center;
  }
`

function About() {
  return (
    <StyledAbout>
      <AboutInner>
        <AboutContent>
          <Subtitle>100% Natural Food</Subtitle>
          <Title>Choose the best healthier way of life</Title>
          <Button $marginTop="1.28rem">
            <div>Explore Now</div>
            <ButtonArrow>
              <img src="./img/common/arrow.svg" alt=""/>
            </ButtonArrow>
          </Button>
        </AboutContent>
        <AboutImgContainer>
          <CorrectedImage src="./img/about/image.png" alt="About image"/>
        </AboutImgContainer>
      </AboutInner>
    </StyledAbout>
  );
}

export default About;
