import styled from "styled-components";
import {Container} from "../../styles/common";

export const StyledHeader = styled(Container)`
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 2rem;
  padding-top: 3rem;
  padding-bottom: 3rem;
  max-width: 1600px;
  @media (max-width: ${props => props.theme.breakpoints.large}) {
    gap: 1.5rem;
  }
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    flex-direction: column;
  }
`

export const MenuList = styled.ul`
  font-family: Roboto, sans-serif;
  font-size: 1.11111rem;
  font-weight: 700;
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 2em;
  @media (max-width: ${props => props.theme.breakpoints.large}) {
    font-size: 1rem;
    gap: 1.5em;
  }
`

export const MenuItem = styled.li`
  transition: all 0.3s ease-in-out;
  position: relative;
`

export const MenuLink = styled.a`
  color: ${props => props.theme.colors.title};
  @media (min-width: ${props => props.theme.breakpoints.medium}) {
    &:hover {
      color: #000000;
    }
  }
`

export const DropdownBtn = styled.button`
  background: none;
  display: flex;
  align-items: center;
  font-family: Roboto, sans-serif;
  font-size: 1.11111rem;
  font-weight: 700;
  color: ${props => props.theme.colors.title};
  @media (max-width: ${props => props.theme.breakpoints.large}) {
    font-size: 1rem;
  }
  @media (min-width: ${props => props.theme.breakpoints.medium}) {
    &:hover {
      color: #000000;
    }
  }
`

export const DropdownBtnIcon = styled.img`
  padding-left: 6px;
  height: 0.4em;
  width: auto;
`

export const DropdownContent = styled.div`
  display: ${props => (props.isOpened ? "flex" : "none")};
  flex-direction: column;
  position: absolute;
  left: -1rem;
  top: 1.8rem;
  font-family: Roboto, sans-serif;
  font-size: 1.11111rem;
  font-weight: 700;
  color: ${props => props.theme.colors.title};
  @media (max-width: ${props => props.theme.breakpoints.large}) {
    font-size: 1rem;
  }
  min-width: 150px;
  z-index: 1;
  box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2);
  background-color: #F1EFF0;
`

export const DropdownLink = styled(MenuLink)`
  padding: 1rem;
  @media (min-width: ${props => props.theme.breakpoints.medium}) {
    &:hover {
      background-color: #E2E9E5;;
    }
  }
`

export const SearchAndCartWrapper = styled.div`
  display: flex;
  align-items: center;
  gap: 0.7rem;
`

export const StyledSearch = styled.div`
  display: flex;
  align-items: center;
  border-radius: 2rem;
  background: #FAFAFA;
  padding: 0.25rem;
`

export const SearchInput = styled.input`
  width: 100%;
  max-width: 240px;
  background-color: inherit;
  color: ${props => props.theme.colors.title};
  font-family: Roboto, sans-serif;
  font-size: 1rem;
  font-weight: 600;
  padding: 0 0.8rem;
`

export const SearchBtn = styled.button`
  background-color: #7EB693;
  border-radius: 50%;
  padding: 0.8rem;
  transition: all 0.3s ease-in-out;
  @media (min-width: ${props => props.theme.breakpoints.medium}) {
    &:hover {
      background-color: ${props => props.theme.colors.title};
    }
  }
  @media (max-width: ${props => props.theme.breakpoints.large}) {
    padding: 0.7rem;
  }
`

export const StyledCart = styled.button`
  display: flex;
  align-items: center;
  background-color: #FFFFFF;
  border-radius: 1.83333rem;
  border: 1px solid #E0E0E0;
  padding: 0.25rem;
  transition: all 0.3s ease-in-out;
  @media (min-width: ${props => props.theme.breakpoints.medium}) {
    &:hover {
      background-color: #E0E0E0;
    }
  }
`

export const CartIcon = styled.div`
  background-color: ${props => props.theme.colors.title};
  border-radius: 50%;
  padding: 0.7rem;
  transition: all 0.3s ease-in-out;
  @media (max-width: ${props => props.theme.breakpoints.large}) {
    padding: 0.55rem;
  }
`

export const CartText = styled.div`
  color: ${props => props.theme.colors.title};
  font-family: Roboto, sans-serif;
  font-size: 1rem;
  font-weight: 600;
  padding: 0 0.6rem;
`
