import styled from "styled-components";

export const Container = styled.div`
  max-width: ${({ theme }) => (theme.container.width)};
  padding: 0 ${({ theme }) => theme.container.paddingX};
  margin: 0 auto;
`;

export const Subtitle = styled.div`

`
