import styled from "styled-components";
import {Container} from "../../styles/common";

export const StyledFooterTop = styled(Container)`
  color: #525C60;
  font-family: 'Open Sans', sans-serif;
  font-size: 1rem;
  line-height: 1.65em;
  font-weight: 400;
  display: flex;
  justify-content: center;
  gap: 2.5rem;
  padding-top: 6rem;
`

export const FooterNavListItem = styled.div`
  padding-bottom: 1rem;
`

export const FooterNavLink = styled.a`
  color: #525C60;
`

export const FooterTitle = styled.div`
  color: #274C5B;
  font-family: Roboto, sans-serif;
  font-size: 1.66667rem;
  font-weight: 700;
  padding-bottom: 1.7rem;
`

export const FooterSubtitle = styled.div`
  font-weight: 700;
`

export const FooterContacts = styled.div`
  text-align: right;
`

export const FooterLogoSection = styled.div`
  text-align: center;
  border-left: 1px solid #D4D4D4;
  border-right: 1px solid #D4D4D4;
  padding: 0 2.3rem;
`

export const FooterLogoText = styled.div`
  max-width: 500px;
  margin-top: 1.2rem;
  margin-bottom: 2.7rem;
`

export const FooterLogoSocialLinks = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.8rem;
`

export const FooterLogoSocialLink = styled.a`
  background-color: #EFF6F1;
  border-radius: 50%;
  width: 3rem;
  height: 3rem;
  position: relative;
`

export const FooterLogoSocialLinkImg = styled.img`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
`

export const StyledFooterBottom = styled.div`
  color: #525C60;
  //font-family: 'Open Sans', sans-serif;
  font-size: 1rem;
  line-height: 1.65em;
  font-weight: 400;
  display: flex;
  justify-content: center;
  gap: 2.5rem;
  padding-top: 6rem;
`
