import React from 'react';
import styled from "styled-components";
import {Container, CorrectedImage, Link, LinkArrow, Title} from "../../styles/common";

const CartTitle = styled(Title)`
  text-align: center;
  background-image: url("../../img/cart/title-background.jpg");
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  padding: 10rem ${props => props.theme.container.paddingX};
  margin-bottom: 5rem;
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    padding: 3rem ${props => props.theme.container.paddingX};
    margin-bottom: 2rem;
  }
`

const CartContent = styled.div`
  text-align: center;
  background: rgba(253, 176, 45, 0.16);;
  padding: 8rem 0 3rem 0;
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    padding: 3rem 0 2rem 0;
  }
  color: ${props => props.theme.colors.title};
  font-family: Roboto, sans-serif;
  font-size: 1.77778rem;
  font-weight: 700;
`

const CartContentInner = styled(Container)`
  max-width: 1600px;
`

const CartOrdersList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`

const CartOrdersListItem = styled.div`
  display: flex;
  
`

const CartOrdersListItemImageContainer = styled.div`
  border-radius: 1.66667rem;
  background: #F9F8F8;
`

const CartSummaries = styled.div`
  margin-top: 2rem;
  display: flex;
  flex-direction: column;
  text-align: right;
  gap: 1.5rem;
`

function Cart() {
  const total = 26;
  const discount = 14;
  return (
    <div>
      <CartTitle>Cart</CartTitle>
      <CartContent>
        <CartContentInner>
          <CartOrdersList>
            <CartOrdersListItem>
              <CartOrdersListItemImageContainer>
                <CorrectedImage src="./img/shop/white-nuts.png" alt="Product name" />
              </CartOrdersListItemImageContainer>

            </CartOrdersListItem>
          </CartOrdersList>
          <CartSummaries>
            <div>Total Cost {total}$</div>
            <div>Discount {discount}$</div>
          </CartSummaries>
          <Link href="#" $paddingX="3.2rem" $marginTop="4rem" $color="#FFFFFF" $backgroundColor={props => props.theme.colors.title}>
            <div>To order</div>
            <LinkArrow>
              <img src="./img/common/arrow.svg" alt=""/>
            </LinkArrow>
          </Link>
        </CartContentInner>
      </CartContent>
    </div>
  );
}

export default Cart;
