import React from 'react';
import styled from "styled-components";
import {Container} from "../../styles/common";

const StyledModal = styled.div`
  height: 100vh;
  width: 100vw;
  background-color: rgba(244, 244, 244, 0.82);
  position: fixed;
  z-index: 10;
  top: 0;
  left: 0;
  display: flex;
  align-items: center;
  transition: 0.3s;
  opacity: ${props => ((props.$active) ? "1" : "0")};
`

const ModalInner = styled.div`
  width: 100%;
  padding: 6rem ${props => props.theme.container.paddingX};
  border-radius: 12px;
  background: #FFFAFA;
  position: relative;
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    padding: 3rem ${props => props.theme.container.paddingX};
  }
`


function Modal({active, setActive}) {
  return (
    <StyledModal onClick={() => setActive(false)} $active={active}>
      <ModalInner onClick={e => e.stopPropagation()}>
        <Container>
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Accusantium adipisci aut deleniti dicta esse, ex facilis, harum illo inventore magnam magni nesciunt quibusdam repellat repudiandae rerum, tempore temporibus! Deleniti, excepturi.
        </Container>
      </ModalInner>
    </StyledModal>
  );
}

export default Modal;
