import styled from "styled-components";
import {Container} from "../../styles/common";

export const StyledMenu = styled.nav`
  font-size: 1rem;
  display: flex;
  align-items: center;
  gap: 2em;
`

export const StyledHeader = styled(Container)`
  display: flex;
`
