import React from 'react';
import styled from "styled-components";
import {CustomLink, LinkArrow, Container, CorrectedImage, Subtitle, Title} from "../../../assets/styles/common";
import arrow from "../../../assets/img/common/arrow.svg"
import background from "../../../assets/img/home/promo/background.svg"
import promoImage from "../../../assets/img/home/promo/image.png"

const StyledPromo = styled.div`
  background-color: #F1EFF0;
  background-image: url("../assets/img/home/promo/background.svg");
  background-repeat: no-repeat;
  background-size: cover;
`

const PromoInner = styled(Container)`
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 2rem;
`

const PromoContent = styled.div`
  max-width: 530px;
  width: 100%;
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    padding: 3rem 0;
    text-align: center;
    max-width: none;
    display: flex;
    flex-direction: column;
    align-items: center;
  }
`

const PromoImgContainer = styled.div`
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    display: none;
  }
`

function Promo() {
  return (
    <StyledPromo background={background}>
      <PromoInner>
        <PromoContent>
          <Subtitle>100% Natural Food</Subtitle>
          <Title>Choose the best healthier way of life</Title>
          <CustomLink href="#" $marginTop="1.28rem">
            <div>Explore Now</div>
            <LinkArrow>
              <img src={arrow} alt=""/>
            </LinkArrow>
          </CustomLink>
        </PromoContent>
        <PromoImgContainer>
          <CorrectedImage src={promoImage} alt="Promo image"/>
        </PromoImgContainer>
      </PromoInner>
    </StyledPromo>
  );
}

export default Promo;
