import React, {useEffect, useState} from 'react';
import styled from "styled-components";
import {Button, Container} from "../../styles/common";

const StyledModal = styled.div`
  height: 100vh;
  width: 100vw;
  background-color: rgba(244, 244, 244, 0.82);
  position: fixed;
  z-index: 10;
  top: 0;
  left: 0;
  align-items: center;
  transition: 0.3s;
  display: ${props => ((props.$active) ? "flex" : "none")};
`

const ModalInner = styled.div`
  width: 100%;
  padding: 6rem ${props => props.theme.container.paddingX};
  border-radius: 12px;
  background: #FFFAFA;
  position: relative;
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    padding: 3rem ${props => props.theme.container.paddingX};
  }
`

const ModalCloseButtonContainer = styled.div`
  position: absolute;
  top: 0;
  right: 0;
`


function Modal({active, setActive}) {
  // block scrolling when modal opened
  useEffect(() => {
    const body = document.querySelector('body');
    if (active) {
      body.style.overflow = 'hidden';
    } else {
      body.style.overflow = 'auto';
    }

    
  }, [active]);
  return (
    <StyledModal onClick={() => setActive(false)} $active={active}>
      <ModalInner onClick={e => e.stopPropagation()}>
        <Container>
          <ModalCloseButtonContainer>
            <Button type="button"
                    $color="#FFFFFF"
                    $backgroundColor={props => props.theme.colors.title}
                    $paddingY="1rem"
                    $paddingX="2rem"
                    onClick={() => setActive(false)}
            >X</Button>
          </ModalCloseButtonContainer>

        </Container>
      </ModalInner>
    </StyledModal>
  );
}

export default Modal;
