import React from 'react';
import styled from "styled-components";
import {Button, ButtonArrow, Container, Subtitle, Title} from "../../styles/common";

const StyledNews = styled(Container)`
  padding-top: 9rem;
  padding-bottom: 9rem;
`

const NewsHeader = styled.div`
  display: flex;
  align-items: end;
  justify-content: space-between;
  gap: 2rem;
`

const NewsHeaderTitleBlock = styled.div`
  max-width: 970px;
`

const NewsHeaderButton = styled(Button)`
  border: 2px solid #274C5B;
  background: #FFFFFF;
  min-width: 250px;
`

function News() {
  return (
    <StyledNews>
      <NewsHeader>
        <NewsHeaderTitleBlock>
          <Subtitle>News</Subtitle>
          <Title>Discover weekly content about organic food, & more</Title>
        </NewsHeaderTitleBlock>
        <NewsHeaderButton>
          <div>More News</div>
          <ButtonArrow>
            <img src="./img/common/arrow.svg" alt=""/>
          </ButtonArrow>
        </NewsHeaderButton>
      </NewsHeader>
    </StyledNews>
  );
}

export default News;
