import React from 'react';
import styled from "styled-components";

const StyledModal = styled.div`
  height: 100vh;
  width: 100vw;
  background-color: rgba(244, 244, 244, 0.82);
  position: fixed;
  z-index: 10;
  top: 0;
  left: 0;
  display: flex;
  align-items: center;
  // transition: 0.3s;
  // opacity: ${props => ((props.$active) ? "1" : "0")};
  // pointer-events: ${props => ((props.$active) ? "all" : "none")};
`

const ModalContext = styled.div`
  width: 100%;
  padding: 0 ${props => props.theme.container.paddingX};
  border-radius: 12px;
  background: #FFFAFA;
  position: relative;
`

function Modal({active, setActive}) {
  return (
    <StyledModal onClick={() => setActive(false)} $active={active}>
      <ModalContext onClick={e => e.stopPropagation()}>
        Lorem ipsum dolor sit amet, consectetur adipisicing elit. Accusantium adipisci aut deleniti dicta esse, ex facilis, harum illo inventore magnam magni nesciunt quibusdam repellat repudiandae rerum, tempore temporibus! Deleniti, excepturi.
      </ModalContext>
    </StyledModal>
  );
}

export default Modal;
