import React, {useState} from 'react';
import {CartContent, CartContentInner, CartOrdersList, CartSummaries, CartTitle} from "../../features/cart/styles";
import {CustomArrowButton} from "../../components/CustomButtonLink/CustomButtonLink";

function Cart(props) {
  const [visibleForm, setVisibleForm] = useState(false);
  const total = 26;
  const discount = 14;

  return (
    <div>
      <CartTitle>Cart</CartTitle>
      <CartContent>
        <CartContentInner>
          <CartOrdersList>
            <CartOrdersListItem
              name="Health Pistachios"
              imageUrl="./img/shop/mung-bean-1.png"
              currentPrice="50"
              beforeDiscountPrice="100"
              amount={1}
            />
          </CartOrdersList>
          <CartSummaries>
            <div>Total Cost {total}$</div>
            <div>Discount {discount}$</div>
          </CartSummaries>
          {visibleForm ? null : (
            <CustomArrowButton type="Button"
                               $paddingX="3.2rem"
                               $marginTop="4rem"
                               $color="#FFFFFF"
                               $backgroundColor={props => props.theme.colors.title}
                               onClick={(e) => setVisibleForm(true)}
            >To order</CustomArrowButton>
          )}
          <CartForm visible={visibleForm}/>
        </CartContentInner>
      </CartContent>
    </div>
  );
}

export default Cart;
