import React from 'react';
import styled from "styled-components";
import {Button, ButtonStyles, Container} from "../../styles/common";

const StyledModal = styled.div`
  height: 100vh;
  width: 100vw;
  background-color: rgba(244, 244, 244, 0.82);
  position: fixed;
  z-index: 10;
  top: 0;
  left: 0;
  align-items: center;
  transition: 0.3s;
  display: ${props => ((props.$active) ? "flex" : "none")};
`

const ModalInner = styled.div`
  width: 100%;
  padding: 6rem ${props => props.theme.container.paddingX};
  border-radius: 12px;
  background: #FFFAFA;
  position: relative;
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    padding: 3rem ${props => props.theme.container.paddingX};
  }
`

const ModalCloseButton = styled.button`
  position: absolute;
  top: 0;
  right: 0;
  ${ButtonStyles}
`


function Modal({active, setActive}) {
  return (
    <StyledModal onClick={() => setActive(false)} $active={active}>
      <ModalInner onClick={e => e.stopPropagation()}>
        <Container>
          <ModalCloseButton

          >X</ModalCloseButton>
        </Container>
      </ModalInner>
    </StyledModal>
  );
}

export default Modal;
