import React, {useState} from 'react';
import Logo from "../common/Logo";
import {
  StyledHeader,
  MenuItem,
  MenuLink,
  MenuList,
  SearchAndCartWrapper,
  StyledSearch,
  SearchInput,
  SearchBtn, StyledCart, CartIcon, CartText, DropdownBtn, DropdownBtnIcon, DropdownContent
} from "./styles";

function Menu() {
  const menuItems = [
    {'title': 'Home', 'href': '/'},
    {'title': 'About', 'href': '/about'},
    {'title': 'Pages', 'href': [
      {'title': 'Page 1', 'href': '/page/1'},
      {'title': 'Page 2', 'href': '/page/2'},
      {'title': 'Page 3', 'href': '/page/3'},
      ]},
    {'title': 'Shop', 'href': '/shop'},
    {'title': 'Projects', 'href': '/projects'},
    {'title': 'News', 'href': '/news'},
  ];

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const handleDropdownClick = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <div>
      <MenuList>
        {menuItems.map(item => {
          if (item.title === 'Pages') {
            return (
              <MenuItem>
                <DropdownBtn onClick={() => handleDropdownClick()}>
                  <span>{item.title}</span>
                  <DropdownBtnIcon src="./img/header/dropdown-icon.svg" alt=""/>
                </DropdownBtn>
                <DropdownContent>
                  {item.href.map(innerItem =>
                    <MenuLink href={innerItem.href}>{innerItem.title}</MenuLink>
                  )}
                </DropdownContent>
              </MenuItem>
            )
          }
          return (
            <MenuItem>
              <MenuLink href={item.href}>{item.title}</MenuLink>
            </MenuItem>
          );
        })}
      </MenuList>
    </div>
  )
}

function Search() {
  return (
    <StyledSearch>
      <SearchInput/>
      <SearchBtn>
        <img src="./img/header/search-icon.svg" alt="Search"/>
      </SearchBtn>
    </StyledSearch>
  )
}

function Cart() {
  const counter = 0;

  return (
    <StyledCart>
      <CartIcon>
        <img src="./img/header/cart-icon.svg" alt=""/>
      </CartIcon>
      <CartText>Cart ({counter})</CartText>
    </StyledCart>
  )
}


function Header() {
  return (
    <>
      <StyledHeader>
        <Logo/>
        <Menu/>
        <SearchAndCartWrapper>
          <Search/>
          <Cart/>
        </SearchAndCartWrapper>
      </StyledHeader>
    </>
  );
}

export default Header;
