import styled from "styled-components";
import {Container} from "../../styles/common";

export const StyledMenu = styled.nav`
  color: #274C5B;
  font-family: Roboto, sans-serif;
  font-size: 1.11111rem;
  font-weight: 700;
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 2em;
`

export const StyledHeader = styled(Container)`
  display: flex;
`
