import React, {useState} from 'react';
import {slide as Menu} from 'react-burger-menu'
import Logo from "../common/Logo";
import {
  StyledHeader,
  MenuItem,
  MenuLink,
  MenuList,
  SearchAndCartWrapper,
  StyledSearch,
  SearchInput,
  SearchBtn, StyledCart, CartIcon, CartText, DropdownBtn, DropdownBtnIcon, DropdownContent, DropdownLink
} from "./styles";

function HeaderMenu() {
  const menuItems = [
    {'title': 'Home', 'href': '/'},
    {'title': 'About', 'href': '/about'},
    {
      'title': 'Pages', 'href': [
        {'title': 'Page 1', 'href': '/page/1'},
        {'title': 'Page 2', 'href': '/page/2'},
        {'title': 'Page 3', 'href': '/page/3'},
      ]
    },
    {'title': 'Shop', 'href': '/shop'},
    {'title': 'Projects', 'href': '/projects'},
    {'title': 'News', 'href': '/news'},
  ];

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const handleDropdownClick = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <div>
      <MenuList>
        {menuItems.map((item, index) => {
          if (item.title === 'Pages') {
            return (
              <MenuItem key={index}>
                <DropdownBtn onClick={() => handleDropdownClick()}>
                  <span>{item.title}</span>
                  <DropdownBtnIcon src="./img/header/dropdown-icon.svg" alt=""/>
                </DropdownBtn>
                <DropdownContent isOpened={isDropdownOpen}>
                  {item.href.map((innerItem, innerIndex) =>
                    <DropdownLink key={innerIndex} href={innerItem.href}>{innerItem.title}</DropdownLink>
                  )}
                </DropdownContent>
              </MenuItem>
            )
          }
          return (
            <MenuItem key={index}>
              <MenuLink href={item.href}>{item.title}</MenuLink>
            </MenuItem>
          );
        })}
      </MenuList>
    </div>
  )
}

function Search() {
  return (
    <StyledSearch>
      <SearchInput/>
      <SearchBtn>
        <img src="./img/header/search-icon.svg" alt="Search"/>
      </SearchBtn>
    </StyledSearch>
  )
}

function Cart() {
  const counter = 0;

  return (
    <StyledCart>
      <CartIcon>
        <img src="./img/header/cart-icon.svg" alt=""/>
      </CartIcon>
      <CartText>Cart ({counter})</CartText>
    </StyledCart>
  )
}

function HamburgerIcon() {
  return (
    <div>
      <svg
        className={styles.iconStyle}
        fill="none"
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth="2"
        viewBox="0 0 24 24"
        stroke="currentColor"
      >
        <path d="M4 6h16M4 12h16M4 18h16"></path>
      </svg>
    </div>
  );
}


class Example extends React.Component {
  showSettings(event) {
    event.preventDefault();


  }

  render() {
    // NOTE: You also need to provide styles, see https://github.com/negomi/react-burger-menu#styling
    return (
      <Menu>
        <a id="home" className="menu-item" href="/">Home</a>
        <a id="about" className="menu-item" href="/about">About</a>
        <a id="contact" className="menu-item" href="/contact">Contact</a>
        <a onClick={this.showSettings} className="menu-item--small" href="">Settings</a>
      </Menu>
    );
  }
}

function Header() {
  return (
    <>
      <StyledHeader>
        <Logo/>
        <HeaderMenu/>
        <SearchAndCartWrapper>
          <Search/>
          <Cart/>
        </SearchAndCartWrapper>
      </StyledHeader>
    </>
  );
}

export default Header;
