import React from 'react';
import styled from "styled-components";
import {Link, LinkArrow, Title} from "../../styles/common";

const CartTitle = styled(Title)`
  text-align: center;
  background-image: url("../../img/cart/title-background.jpg");
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  padding: 10rem ${props => props.theme.container.paddingX};
  margin-bottom: 5rem;
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    padding: 3rem ${props => props.theme.container.paddingX};
    margin-bottom: 2rem;
  }
`

const CartContent = styled.div`
  text-align: center;
  background: rgba(253, 176, 45, 0.16);;
  padding: 8rem ${props => props.theme.container.paddingX} 3rem ${props => props.theme.container.paddingX};
  @media (max-width: ${props => props.theme.breakpoints.medium}) {
    padding: 3rem ${props => props.theme.container.paddingX} 2rem ${props => props.theme.container.paddingX};
  }
  color: ${props => props.theme.colors.title};
  font-family: Roboto, sans-serif;
  font-size: 1.77778rem;
  font-weight: 700;
`

const CartOrderedList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`

const CartSummaries = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: right;
  gap: 1.5rem;
`

function Cart() {
  return (
    <div>
      <CartTitle>Cart</CartTitle>
      <CartContent>
        <CartSummaries>
          <div>Total Cost 26$</div>
          <div>Discount 14$</div>
        </CartSummaries>
        <Link href="#" $marginTop="4rem" $color="#FFFFFF" $backgroundColor={props => props.theme.colors.title}>
          <div>To order</div>
          <LinkArrow>
            <img src="./img/common/arrow.svg" alt=""/>
          </LinkArrow>
        </Link>
      </CartContent>
    </div>
  );
}

export default Cart;
