import React from 'react';
import styled from "styled-components";
import {Container, Subtitle, Title} from "../../styles/common";

const StyledTestimonials = styled.div`
  background-image: url("../../img/testimonials/background.jpg");
  background-repeat: no-repeat;
  background-size: cover;
  padding: 9rem 0;
  display: flex;
`

const TestimonialsInner = styled(Container)`
  text-align: center;
`

const TestimonialReview = styled.div`
  max-width: 780px;
  
`

const TestimonialReviewImg = styled.img`
  border-radius: 50%;
  width: 115px;
  height: 115px;
`


function Testimonials(props) {
  return (
    <StyledTestimonials>
      <TestimonialsInner>
        <Subtitle>Testimonial</Subtitle>
        <Title>What Our Customer Saying?</Title>
        <TestimonialReview>
          <TestimonialReviewImg src="./img/testimonials/customer-photo.jpg" alt="Client" />
        </TestimonialReview>
      </TestimonialsInner>
    </StyledTestimonials>
  );
}

export default Testimonials;
