import React from 'react';
import styled from "styled-components";
import {CorrectedImage, Title} from "../../styles/common";

const StyledProductCard = styled.a`
  border-radius: 1.66667rem;
  background: #F9F8F8;
  padding: 1.6rem 1.6rem 0.95rem;
  display: flex;
  flex-direction: column;
  align-items: start;
`

const ProductCategory = styled.div`
  padding: 0.33rem 0.66rem;
  border-radius: 0.44444rem;
  background: ${props => props.theme.colors.title};
  color: #FFFFFF;
  font-size: 0.83333rem;
  font-weight: 600;
  margin-bottom: 0.72rem;
`

const ProductName = styled(Title)`
  font-size: 1.11111rem;
  font-weight: 600;
  margin-top: 0.1rem;
  margin-bottom: 0.72rem;
`

const ProductStats = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;
  padding-top: 0.33rem;
  border-top: 1px solid #DEDDDD;
`

const ProductPrice = styled.div`
  color: ${props => props.theme.colors.title};
  font-family: "Open Sans", sans-serif;
  font-size: 1rem;
  font-weight: 700;
`

const ProductDiscountPrice = styled(ProductPrice)`
  
`

function ProductCard({}) {
  return (
    <StyledProductCard>
      <ProductCategory>Vegetable</ProductCategory>
      <CorrectedImage src={"./img/shop/cabbage.png"} alt={"Image name"} />
      <ProductName>Cabbage</ProductName>
      <ProductStats>
        <div>
          <ProductPrice>$13.00</ProductPrice>
        </div>
        <img src="./img/common/stars.svg" alt="5 start" />
      </ProductStats>
    </StyledProductCard>
  );
}

export default ProductCard;
