import styled from "styled-components";

const StyledModal = styled.div`
  height: 100vh;
  width: 100vw;
  background-color: rgba(0, 0, 0, 0.4);
  position: fixed;
  top: 0;
  left: 0;
`

const ModalContext = styled.div`
  padding: 20px;
  border-radius: 12px;
  background-color: #FFFFFF;
`

function Modal({active, setActive}) {
  return (
    <StyledModal>

    </StyledModal>
  );
}

export default Modal;
